<?php include("../config.php") ?><?php	if(@$ac=='up') {		$sql = mysql_query("update metas set descricao = '$descricao', quantidade = '$quantidade', ss = '$ss', valor = '$valor' where id = '$id'");		if($sql) {			echo '					<center>					<div class="alert alert-success">						<strong>Atenção!</strong> Informações alteradas com sucesso.					</div>				</center>			';		}				else {			echo '<p class="text-danger">'.mysql_error().'</p>';		}		exit;	}?><div class="ajax"></div><div class="panel panel-default"><div class="panel-heading">Editar Meta</div><div class="panel-body"><?php	$sql = mysql_query("select * from metas where id = '$id'");	while($l = mysql_fetch_array($sql)) { extract($l);		echo '<form action="javascript:void(0)" onSubmit=\'post(this,"gestor/editar-meta.php?ac=up&id='.$id.'",".ajax")\'>';			echo '<label>Descricao: <input type="text" name="descricao" value="'.$descricao.'" class="form-control" required></label><br/>';			echo '<label>Meta SS: <input type="text" name="ss" value="'.$ss.'" class="form-control" required></label><br/>';			echo '<label>Quantidade: <input type="text" name="quantidade" value="'.$quantidade.'" class="form-control" required></label><br/>';			echo '<label>Valor: <input type="text" name="valor" value="'.$valor.'" class="form-control" required></label><br/>';			echo '<label><input type="submit" value="Salvar" class="btn btn-success"></label>';		echo '</form>';	}?></div></div>