<?php include("../config.php");include("../validar_session.php");date_default_timezone_set('America/Sao_Paulo');$today = getdate(); $meses = array (1 => "Janeiro", 2 => "Fevereiro", 3 => "Março", 4 => "Abril", 5 => "Maio", 6 => "Junho", 7 => "Julho", 8 => "Agosto", 9 => "Setembro", 10 => "Outubro", 11 => "Novembro", 12 => "Dezembro");$mesnum = $today["mon"];if(!isset($pg)){$pg = 1;}?><script type="text/javascript">$(document).ready(function(){	$(".decimal").maskMoney({precision:2})	$('.sel').multiselect({      buttonClass: 'btn btn-sm', 	  numberDisplayed: 1,	  maxHeight: 300,	  includeSelectAllOption: true,	  selectAllText: "Selecionar todos",	  enableFiltering: true,	  enableCaseInsensitiveFiltering: true,	  selectAllValue: 'multiselect-all'	}); });</script><script>  $(document).ready(function() {    $("#btnPrint").printPage();		$("#campoAno").mask("9999");  });</script><style type="text/css">@media all {	.etiqueta{		font-weight:bold;		text-align:center;		font-size:13px;		margin:10px;		padding:10px;		width:99.00mm;		height:40.10mm;		background:#fcf9f9;		word-wrap: break-word;	}	.all-page{		width:100%;		height:100%;	}}@media print{	.etiqueta{		margin: 0 auto;		margin:5px !important;		margin-right:5px !important;	}}</style><?php if(@$ac == 'localizar') { 	foreach($st as $sts) { @$stu .= $sts.','; } $stu = substr($stu,0,-1);	foreach($ob as $obs) { @$obu .= $obs.','; } $obu = substr($obu,0,-1);	foreach($fu as $fus) { @$fuu .= $fus.','; } $fuu = substr($fuu,0,-1);	$mes = $meses[$mess];	$quantidade = 14;	$inicio = ($pg * $quantidade) - $quantidade;	$topo = mysql_query("select *,equipes.obra as obra_eq, rh_funcionarios.obra as obra_fun from equipes, rh_funcionarios where equipes.id = rh_funcionarios.equipe and rh_funcionarios.situacao in ($stu) and rh_funcionarios.obra in ($obu) and rh_funcionarios.nome LIKE '%$busca%' AND rh_funcionarios.id in ($fuu) ORDER BY rh_funcionarios.nome LIMIT $inicio, $quantidade") or die (mysql_error());	echo '<div class="all-page col-xs-12">';	while ($l = mysql_fetch_array($topo)) { extract($l);		echo '<div class="col-xs-6" style="margin-bottom:12px;">';		echo '<table class="etiqueta" style="border-radius:10px; ">';				echo '  				<tr><td>Polêmica Serviços Básicos LTDA</td></tr>				<tr><td>CNPJ: 61.870.101/0001-08</td></tr>				<tr><td>Obra: <small>'.mysql_result(mysql_query("select * from notas_obras where id = $obra_fun"),0,"descricao").'</small> Ref. '.$mes.'/'.$ano.'</td></tr>				<tr><td>Nome: '.$nome.'</td></tr>				<tr><td>Função: <small>'.mysql_result(mysql_query("select * from rh_funcoes where id = $funcao"),0,"descricao").'</small></td></tr>				<tr><td>Horario: Seg. a Qui. '.$trabalho_entrada.' as '.$trabalho_saida.' Sex. '.$sexta_entrada.' as '.$sexta_saida.'</td></tr>				<tr><td>Descanso/Almoço: '.implode("as",explode("-",$trabalho_refeicao)).'</td></tr>';        echo '</table>';		echo '</div>';	}	echo '</div>';} else { ?><div class="hidden-print">		<h3 style="font-family: 'Oswald', sans-serif;letter-spacing:5px; margin-bottom;10px;"> 			<img class="logo-print" src="http://polemicalitoral.com.br/guaruja/imagens/logo.png" style="float:left;" width="60px"/>			<p>ETIQUETA<small> FUNCIONARIOS</small><a a href="javascript:window.print()" style="letter-spacing:5px; margin-top:10px; margin-right:10px;" class="hidden-xs hidden-print pull-right btn btn-warning btn-sm"> <span class="glyphicon glyphicon-print" aria-hidden="true"></span>&nbsp;Imprimir</a></p>		</h3><hr/><div class="well well-sm" style="padding:10px 10px 5px 10px;"><form action="javascript:void(0)" onsubmit="post(this,'rh/fun-etiquetas2.php?ac=localizar','.retorno')">	<label>Funcionario:<input type="text" name="busca" placeholder="Digite o nome do funcionario" size="100" class="form-control input-sm"></label>	<label>		<select name="fu[]" class="sel" multiple="multiple">		<?php 				$rh = mysql_query("select * from rh_funcionarios WHERE obra IN($obra_usuario)"); 				while($l=mysql_fetch_array($rh)) {					echo '<option value="'.$l['id'].'" selected>'.$l['nome'].'</option>';  					}  			?>		</select>	</label>	<label>Mês:		<select name="mess" class="form-control input-sm">		<?php		$m = 1;		while($m <= 12){			if($m == $mesnum){			echo '<option value="'.$m.'" selected>'.$meses[$m].'</option>';			}else{			echo '<option value="'.$m.'">'.$meses[$m].'</option>';			}$m += 1;		}				?>		</select>	</label>	<label>Ano: <input type="text" id="campoAno" value="<?php echo $today['year'] ?>" name="ano" placeholder="" size="4" maxlength="4" class="form-control input-sm">	</label>	<label>Pagina: <br/>		<select name="pg" style="" class="form-control input-sm">		<?php 		for($i = 1; $i <= 9; $i++){			echo '<option value="'.$i.'">'.$i.'</option>';		}		?>		</select>	</label>	<label>Obra: <br/>	<select name="ob[]" class="sel" multiple="multiple">		<?php 				$situacaos = mysql_query("select * from notas_obras WHERE id IN($obra_usuario)"); 				while($l=mysql_fetch_array($situacaos)) {					echo '<option value="'.$l['id'].'" selected>'.$l['descricao'].'</option>';  					}  			?>		</select>	</label>	<label>Situação: <br/>	<select name="st[]" class="sel" multiple="multiple">		<option value="0"> SEM SITUACAO </option>		<?php 				$situacaos = mysql_query("select * from  rh_situacao order by descricao asc"); 				while($l=mysql_fetch_array($situacaos)) {					if($l['id']=='6') { 					echo '<option value="'.$l['id'].'" selected>'.$l['descricao'].'</option>'; }					else { echo '<option value="'.$l['id'].'">'.$l['descricao'].'</option>'; } 						}  			?>		</select>	</label>	<input type="submit" value="Pesquisar" style="width:100px" class="btn btn-success btn-sm"></form></div></div><div class="retorno"></div><?php } ?>